import React, { useEffect, useState } from "react";
import {
  Button,
  Checkbox,
  Form,
  Input,
  Layout,
  Modal,
  Space,
  Table,
} from "antd";
import type { ColumnsType } from "antd/es/table";
// import Layouts from "../../components/layouts";
// import { styled } from 'styled-components';
import axios from "axios";
import api from "../API/axios";


enum STATUS {
  EDIT,
  CREATE,
}
interface DataType {
  key: string;
  name: string;
  age: number;
  address: string;
  tags: string[];
}

const HomePage = () => {

  const [data, setData] = useState([]);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [form] = Form.useForm();
  
  const [status, setStatus] = useState<STATUS>(STATUS.CREATE);
  // const [accountID, setAccountID] = useState()
  const handleSubmit = () => {

  };

  // const admin = async () => {
  //   const response = await api.get("/")
  //   return response.data.data;
  // }

  // useEffect(() => {
  //   admin();
  //   console.log('admin', admin());
  //   },[])


    const onFinish = async (values: any) => {
      const newValue = {
        ...values,
        AccountID: Math.random(), // có thể dùng timestamp để hiển thị giá trị.
      }
      if (status === STATUS.CREATE) {
      const response = await axios.post('http://localhost:8888/account', newValue);

      if (response.status === 200) {
        console.log('response', response);
        
        // B1: alert  
        // B2: goi lai ham fetchData()
      console.log('CREATE');

        fetchData();
      }
    } else {
      console.log('EDIT');

    }
  };

  const openCreate = () => {
    setIsModalOpen(true);
    setStatus(STATUS.CREATE);
  };

  const fetchData = async () => {
    //Cach 1: Call bang FETCH
    // const response = await fetch("http://localhost:8888/account", {method: 'GET'});
    // const jsonData = await response.json();
    // if (jsonData.status === 'success') {
    //   setData(jsonData.data)
    // }

    // Cach 2: Call bang Axios
    const response = await axios.get("http://localhost:8888/account");
    if (response.status === 200) {
      setData(response.data.data);
    }
  };

  const handleEdit = (record: any) => {
    setStatus(STATUS.EDIT);
    form.setFieldsValue(record);
    setIsModalOpen(true);
  };

  fetchData();

  useEffect(() => {
    fetchData();
  }, []);

  const handleDelete = () => {};

  const handleReset = () => {};

  const onFinishFailed = (errorInfo: any) => {
    console.log("Failed:", errorInfo);
  };

  const handleOk = () => {
    setIsModalOpen(false);
  };
  const handleCancel = () => {
    setIsModalOpen(false);
  };

  const columns: ColumnsType<DataType> = [
    {
      title: "ID",
      dataIndex: "AccountID",
      key: "AccountID",
    },
    {
      title: "Name",
      dataIndex: "Username",
      key: "Username",
    },
    {
      title: "FullName",
      dataIndex: "FullName",
      key: "FullName",
    },
    {
      title: "Address",
      dataIndex: "Address",
      key: "Address",
    },
    {
      title: "action",
      dataIndex: "address",
      key: "address",
      render: (_, record) => (
        <>
          <Button
            type="primary"
            style={{ marginRight: 8 }}
            onClick={() => handleEdit(record)}
          >
            Edit
          </Button>
          <Button type="primary" danger onClick={handleDelete}> Delete </Button>
        </>
      ),
    },
  ];
  return (
    <>
    {/* {columns.map((colum) => {
              // console.log('a: ', colum.title);
              
            })} */}
      <Button type="primary" onClick={openCreate}> Thêm người dùng mới </Button>
      <Table dataSource={data} columns={columns} bordered/>
      <Modal title={ status === STATUS.CREATE ? "Thêm người dùng mới" : "Cập nhật người dùng" }
        open={isModalOpen}
        onOk={handleOk}
        onCancel={handleCancel}
        footer={[
        <Button key="back" type="primary" onClick={handleCancel} danger> Cancel </Button>, 
        ]}>
        <Form
          form={form}
          name="basic"
          labelCol={{ span: 24 }}
          wrapperCol={{ span: 24 }}
          initialValues={{ remember: true }}
          onFinish={onFinish}
          onFinishFailed={onFinishFailed}
          autoComplete="off"
        >
          <Form.Item label="AccountID" name="AccountID" rules={[{required: true, message: 'Please input your account ID!'}]}>
            <Input />
          </Form.Item>
          <Form.Item label="Username" name="Username" rules={[{required: true, message: "Please input your username!"}]}>
            <Input />
          </Form.Item>
          <Form.Item label="FullName" name="FullName" rules={[{required: true, message: "Please input your password!"}]}>
            <Input />
          </Form.Item>
          <Form.Item label="Email" name="Email" rules={[{required: true, message: "Please input your username!" }]}>
            <Input />
          </Form.Item>
          <Form.Item label="Address" name="Address" rules={[{required: true, message: "Please input your username!" }]}>
            <Input />
          </Form.Item>
          <Form.Item wrapperCol={{ offset: 0, span: 16 }}>
            <Button style={{ marginRight: 8 }} type="primary" htmlType='submit'>
              Submit
            </Button>
            <Button onClick={handleReset}>Reset</Button>
          </Form.Item>
        </Form>
      </Modal>
    </>
  );
};

export default HomePage;
